import React, { Component }  from 'react'
import Carta from '../Carta/Carta'
import './Tablero.css';

export default class Tablero extends Component {
    render() {
      return (
        <div className="tablero">
          {
            this.props.baraja
              .map((carta, index) => {
                let estaSiendoComparada =false
                this.props.parejaSeleccionada.map((index) => {
                  if(index === carta){
                    estaSiendoComparada = true
                  }
                })
                console.log("Esta siendo comparada: ", estaSiendoComparada)
                return <Carta
                  key={index} 
                  icono={carta.icono}
                  estaSiendoComparada={estaSiendoComparada}
                  seleccionarCarta={() => this.props.seleccionarCarta(carta)}
                  fueAdivinada={carta.fueAdivinada}
                />;
              })
          }
        </div>
      ); 
    }
};
