import React, { Component } from 'react';
import './App.css';
import Header from './Header/Header'
import Tablero from './Tablero/Tablero'
import construirBaraja from './utils/construirBaraja';


let getEstadoInicial = () => {
  let baraja = construirBaraja();
  return {
    baraja,
    parejaSeleccionada: [],
    estaComparando: false,
    numeroDeIntentos: 0    
  };
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = getEstadoInicial();
  }

  render() {
    return (
      <div className="App">
        <Header 
          numeroDeIntentos={this.state.numeroDeIntentos}
          resetearPartida={() => this.resetearPartida()}
        />
        <Tablero 
          baraja={this.state.baraja}
          parejaSeleccionada={this.state.parejaSeleccionada}
          seleccionarCarta={(carta) => this.seleccionarCarta(carta)}
        />
      </div>
    );
  }

  seleccionarCarta(carta) {
    if (
      this.state.estaComparando ||
      this.state.parejaSeleccionada.indexOf(carta) > -1 ||
      carta.fueAdivinida
    ) {
      return;
    }

    let parejaSeleccionada = [...this.state.parejaSeleccionada, carta];

    this.state.parejaSeleccionada = parejaSeleccionada


    console.log(this.state.parejaSeleccionada)
    if (parejaSeleccionada.length === 2) {
      console.log("Entra")
      this.compararPareja(parejaSeleccionada);
      console.log("Sale")
    }
    console.log("Bandera 1")
    console.log(parejaSeleccionada)

  }

  compararPareja(parejaSeleccionada) {

    //this.setState({estaComparando: true});

    this.state.estaComparando = true

    setTimeout(() => {
      let [primeraCarta, segundaCarta] = parejaSeleccionada;
      let baraja = this.state.baraja;

      if (primeraCarta.icono === segundaCarta.icono) {
        baraja = baraja.map((carta) => {
          if (carta.icono !== primeraCarta.icono) {
            return carta;
          }

          return {...carta, fueAdivinada: true};
        });
      }

      this.verificarSiHayGanador(baraja);

      this.state.baraja = baraja
      this.state.estaComparando = false
      this.state.parejaSeleccionada = []

      /*this.setState({baraja})
      this.setState({estaComparando: false})
      this.setState({
        parejaSeleccionada: []
      })*/
    }, 1000)
  }

  verificarSiHayGanador(baraja) {
    if (
      baraja.filter((carta) => !carta.fueAdivinada).length === 0
    ) {
      alert(`Ganaste en ${this.state.numeroDeIntentos} intentos!`);
    }
  }

  resetearPartida() {
    this.setState(
      getEstadoInicial()
    );
  }
}

