import './App.css';
import Header from './Header/Header'
import Tablero from './Tablero/Tablero'
import contruirBaraja from './utils/construirBaraja';


const getEstadoInicial = () => {
  const baraja = contruirBaraja();
  return{
    baraja,
    parejaSeleccionada: [],
    estaComparando: false
  };
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = getEstadoInicial();
  }

  render() {
    return (
      <div className="App">
        <Header />
        <Tablero 
          baraja={this.state.baraja}
          parejaSeleccionada={this.state.parejaSeleccionada}
          seleccionarCarta={(carta) => this.seleccionarCarta(carta)}
        />
      </div>
    );
  }

  seleccionarCarta(carta) {
    if (
      this.state.estaComparando ||
      this.state.parejaSeleccionada.indexOf(carta) > -1 ||
      carta.fueAdivinida
    ) {
      return;
    }

    const parejaSeleccionada = [...this.state.parejaSeleccionada, carta];
    this.setState({
      parejaSeleccionada
    });

    if (parejaSeleccionada.length === 2) {
      this.compararPareja(parejaSeleccionada);
    }
  }

  compararPareja(state){
    state.estaComparando = true;

    setTimeout(() => {
      const [primeraCarta, segundaCarta] = state.parejaSeleccionada;
      let baraja = state.baraja;

      if( primeraCarta.icono === segundaCarta.icono){
        baraja = baraja.map((carta) => {
          if(carta.icono !== primeraCarta.icono){
            return carta;
          }
          console.log("Pareja encontrada")
          carta.fueAdivinada = true
          return {...carta};
        });
      }

      state.parejaSeleccionada = []
      state.baraja = baraja
      state.estaComparando = false

    }, 1000)

  }
}

